//jxl is a jar which helps connect to the excel toread and write data for me
//since i am a tester i read data
import jxl.*
def filepath = "C://Users//user//Desktop//ASSIGNMENTS//testdata2xls.xls"
//where the file is to read
File Excel = new File(filepath)
def email,firstname,lastname
//excelsheet basically it contains workbook and sheets
//i have to get the workbook
Workbook wb = Workbook.getWorkbook(Excel)
//i am trying to get the sheet
Sheet sh = wb.getSheet("Sheet1")
def row = sh.getRows().toInteger()
def column = sh.getColumns().toInteger()
log.info "the number of rows are $row"
log.info "the number of columns are $column"
//i want to fetch the data in each row and column
for(def i=0;i<row;i++)
{
	for(def j=0;j<column;j++)
	{
		if(j==0)
		{
			email = sh.getCell(j,i).getContents()
		}
		else if(j==1)

		{
			firstname = sh.getCell(j,i).getContents()
		}
		else{
			lastname = sh.getCell(j,i).getContents()
			}
	}
	log.info "$email"
	log.info "$firstname"
	log.info "$lastname"


}

